import React, { useState } from 'react'

function App() {
    const [expense,setExpense] = useState();
    const [amount,setAmount] = useState();
    const [transaction,setTransaction]=useState([]);


    const addExpense = ()=>{
      if(!expense || !amount)return;

      const newTransaction = {
        id:transaction.length+1,
        title:expense,
        amount:amount
      } 
      setTransaction([...transaction,newTransaction]);
      setAmount('');
      setExpense('');
      console.log('transaction Added')


    }
    const deleteTransaction = (id)=>{
        setTransaction(transaction.filter((element)=>element.id !== id))
    }

  return (
    <div >
      <h1 className="text-3xl font-bold underline bg-gray-800" >Expense Tracker</h1>
      <div>


      <input className= 'placeholder:italic placeholder:text-slate-400 block bg-white w-full border border-slate-300 rounded-md py-2 pl-9 pr-3 shadow-sm focus:outline-none focus:border-sky-500 focus:ring-sky-500 focus:ring-1 sm:text-sm'type="text" value={expense} placeholder='Enter Description' onChange={(e)=>setExpense(e.target.value)}/>
      <input type="number" value={amount} placeholder='Enter Amount' onChange={(e)=>setAmount(e.target.value)} />
      </div><button onClick={addExpense}>Add Expense</button>
      <ul>
        {
            transaction.map((element)=>{
                return <li key={element.id}><span>{element.title}</span><span>{element.amount}</span><button onClick={()=>{deleteTransaction(element.id)}}>Delete</button></li>
            })
        }
      </ul>

    </div>
  )
}

export default App
