import React, { useContext, useState } from 'react';
import { UserContext } from '../Context/ProviderContext';

function AddTransaction() {
  const [expense, setExpense] = useState('');
  const [amount, setAmount] = useState('');

  const { addTransaction } = useContext(UserContext); // Destructure addTransaction from UserContext

  const addExpense = () => {
    if (!expense || !amount) return;

    const newTransaction = {
      id: Math.floor(Math.random() * 100000000),
      text: expense,
      amount: parseFloat(amount),
    };

    addTransaction(newTransaction);
    setExpense('');
    setAmount('');
  };

  return (
    <div className='max-w-40 mt-10 p-6 bg-white shadow-md rounded-lg '>
      <input
        type="text"
        id="text"
        value={expense}
        placeholder="Enter Description"
        className='w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none '
        onChange={(e) => setExpense(e.target.value)}
      />
      <input
        type="number"
        id="amount"
        value={amount}
        placeholder="Enter Amount"
        onChange={(e) => setAmount(e.target.value)}
      />
      <button
        className="bg-sky-500 hover:bg-cyan-600"
        onClick={addExpense}
      >
        Add Expense
      </button>
    </div>
  );
}

export default AddTransaction;
