import React, { useContext } from 'react'
import { UserContext } from '../Context/ProviderContext'




function History() {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount);
  };
  const {transactions,deleteTransaction} = useContext(UserContext);
  console.log(transactions)
   

  return (
<div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg border border-gray-200">
  <h4 className="text-xl font-semibold mb-4">History</h4>
  <ul className="space-y-2">
    {transactions.map((element) => (
      <li
        key={element.id}
        className={`flex items-center justify-between p-4 border rounded-md relative ${
          element.amount >= 0 ? 'bg-green-50 border-green-200 text-green-800' : 'bg-red-50 border-red-200 text-red-800'
        }`}
      >
        <div className="flex-1 flex justify-between">
          <span className="font-medium">{element.text}</span>
          <span className={`font-bold ${element.amount >= 0 ? 'text-green-700' : 'text-red-700'}`}>{formatAmount(element.amount)}</span>
        </div>
        <button
          onClick={() => deleteTransaction(element.id)}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-600 hover:text-gray-900 transition-colors duration-200"
        >
          Delete
        </button>
      </li>
    ))}
  </ul>
</div>


  

  
  )
}

export default History
