import React, { useContext } from 'react'
import { UserContext } from '../Context/ProviderContext'




function History() {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount);
  };
  const {transactions,deleteTransaction} = useContext(UserContext);
  console.log(transactions)
   

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg">
    <h4 className="text-xl font-semibold mb-4">History</h4>
    <ul className="space-y-2">
      {transactions.map((element) => (
        <li
          key={element.id}
          className="flex justify-between items-center p-2 border border-gray-300 rounded-md hover:bg-gray-100 relative"
        >
          <span className="flex-1">{element.text}</span>
          <span className="mr-4">{formatAmount(element.amount)}</span>
          <button
            onClick={() => deleteTransaction(element.id)}
            className="absolute right-2 top-1/2 transform -translate-y-1/2 opacity-0 hover:opacity-100 transition-opacity duration-200"
          >
            Delete
          </button>
        </li>
      ))}
    </ul>
  </div>
  )
}

export default History
