import React, { useContext } from 'react'
import { UserContext } from '../Context/ProviderContext'




function History() {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount);
  };
  const {transactions,deleteTransaction} = useContext(UserContext);
  console.log(transactions)
   

  return (
    <div>
      <h4>History</h4>
       <ul>
     {
          transactions.map((element)=>{
                return <li key={element.id}><span>{element.text}</span><span>{formatAmount(element.amount)}</span><button onClick={()=>{deleteTransaction(element.id)}}>Delete</button></li>
            })
        }
     </ul> 
    </div>
  )
}

export default History
